import React, {useState} from 'react';
const api = {
  key: "1743a7b820524ef215c02272bff8f62f",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState('');
  const month = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const day = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday','Friday','Saturday'];

  const todayDate = (weather) => {

    let date = weather.getDate();
    let forToday = day[weather.getDay()];
    let forMonth = month[weather.getMonth()]
    let forYear = weather.getFullYear();

    return `${forToday} ${date} ${forMonth} ${forYear}`

    let searchBar = (evt) => {
      if (evt.key === "Enter") {
        fetch(`${api.base}weather?q=$`)
      }
    }
  }

  return (
    <div className="App">
      <main>
        <div className="search">
          <input type="text" className="search-bar" placeholder="Search"/>
        </div>
          <div className="information">
            <div className="location">Brooklyn, US</div>
            <div className="date">{todayDate(new Date())}</div>
          </div>
            <div className="weather">
              <div className="temperature">
              </div>
              <div className="today-weather"></div>
            </div> 
      </main>
    </div>
  );
}

export default App;
